mapper.delete:
DELETE FROM likes
WHERE
pid = #{pid} and mid = #{mid}

mapper.select:
SELECT *
FROM likesVO_VIEW
WHERE pid = #{pid} and mid = #{mid}


 select pid,mid,ltime,pname,bname
  from(
  select /* +INDEX(tbl_reply idx_reply) */ 
  rownum rn ,pid,mid,ltime,pname,bname
  from likesVO_VIEW
  where mid = #{mid}

  and rownum <= #{cri.pageNum} * #{cri.amount}
  )
where rn > (#{cri.pageNum} - 1) * #{cri.amount}
